import React, { useState } from 'react';
import example1 from '../assets/example1.jpg';

function Work() {
  const [slideIndex, setSlideIndex] = useState(0);
  const slides = [example1, example1, example1]; // Add more images here

  const prevSlide = () => {
    setSlideIndex(slideIndex === 0 ? slides.length - 1 : slideIndex - 1);
  };

  const nextSlide = () => {
    setSlideIndex(slideIndex === slides.length - 1 ? 0 : slideIndex + 1);
  };

  return (
    <div className='h-screen bg-red-400 flex justify-center items-center'>
      <img className='h-36 mx-2 my-2' src={slides[slideIndex]} alt="Example of work" />
      <div className='absolute left-0 top-1/2 transform -translate-y-1/2'>
        <button onClick={prevSlide}>Prev</button>
      </div>
      <div className='absolute right-0 top-1/2 transform -translate-y-1/2'>
        <button onClick={nextSlide}>Next</button>
      </div>
    </div>
  );
}

export default Work;
